import React, {Component} from 'react';
import axios from 'axios';

const API_URL = 'https://jsonplaceholder.typicode.com/posts';
class demo extends Component {
    constructor() {
    super();
    this.state = {
        posts : [],
        userId : "",
        title: "",
        body: "",
    }
    }
    componentDidMount = () => this.getPost();
    getPost = async() =>{
        try{
            const {data} = await axios.get(API_URL);
            //console.log(data);
            this.setState({posts:data});
        }
        catch(err){
            console.log(err);
        }
    }
    handle = async (event) => {
      this.setState({[event.target.name]:event.target.value})
      //console.log(this.state)
     
    }
    update = async (event) => {
        //console.log(UserId,Title,Body);
        event.preventDefault();
        const {UserId,Title,Body} = [event.target.value]
        const post = {
            userId: UserId,
            title:  Title ,
            body:  Body ,
        }
        console.log("inside update")
        console.log(event.target.value)
        try{
             await axios.put(`${API_URL}/${this.state.userId}`,post)  
             //const {data} = await axios.get(API_URL);
             //console.log(data);
             let posts = [...this.state.posts, post];
             //console.log(posts);
             this.setState({posts});
            }
            catch(err){
              console.log(err);
            }
    }
    view = async (Id) => {
      const {data} = await axios.get(`${API_URL}/${Id}`)  ;
      console.log(data);
      alert("userId" + data.id + " " + "Title" + data.title + " " + "Body" + data.body);    
    }
    delete = async(postId) => {
        try {
         await axios.delete(`${API_URL}/${postId}`);
        let posts = [...this.state.posts];
        posts =  posts.filter(({id}) => id !== postId); 
        this.setState({posts});
       // console.log(posts);
       // console.log(postId)
        }
        catch(err) {
            console.log(err);
        }
    }
    render(){
        return(
            <>
            <form onSubmit={this.update}>
                <label>userId
                <input type="number" value={this.state.userId.value} name="userId" onChange={this.handle} />
                </label>
                <label>Title
                <input type="text" value={this.state.title.value} name="title" onChange={this.handle} />
                </label>
                <label>Body
                <input type="text" value={this.state.body.value} name="body" onChange={this.handle} />
                </label>
                <input type="submit" value="Submit" />
            </form>
            <table>
                <thead>
                    <th>userId</th>
                    <th>Id</th>
                    <th>Title</th>
                    <th>Body</th>
                    <th>Action</th>
                </thead>
                <tbody>
                    {this.state.posts.map(({userId, id, title, body})=> {
                    return(
                    <tr key={id}>
                        <td>{userId}</td>
                        <td>{id}</td>
                        <td>{title}</td>
                        <td>{body}</td>
                        <td><button onClick={()=>this.delete(id)}>Delete</button></td>
                        <td><button onClick={()=>this.view(id)}>View comments</button></td>
                        <td><button>View User</button></td>
                    </tr>    
                    );
    })
    }
                </tbody>
            </table>
            </>
        ) 
    }
}
export default demo;
