import React, {Component} from 'react';
import axios from 'axios';

const API_URL = 'https://jsonplaceholder.typicode.com/posts';
class demo extends Component {
    constructor() {
    super();
    this.state = {
        posts : [],
    }
    }
    componentDidMount = () => this.getPost();
    getPost = async() =>{
        try{
            const {data} = await axios.get(API_URL);
            console.log(data);
            this.setState({posts:data});
        }
        catch(err){
            console.log(err);
        }
    }
    
    delete = async(postId) => {
        try {
         await axios.delete(`${API_URL}/${postId}`);
        let res = [...this.state.posts];
        res =  res.filter(({id}) => id !== postId); 
        this.setState({posts : res});
        console.log(posts);
        console.log(postId)
        }
        catch(err) {
            console.log(err);
        }
    }
    render(){
        return(
            <table>
                <thead>
                    <th>userId</th>
                    <th>Id</th>
                    <th>Title</th>
                    <th>Body</th>
                    <th>Action</th>
                </thead>
                <tbody>
                    {this.state.posts.map(({userId, id, title, body})=> {
                    return(
                    <tr key={id}>
                        <td>{userId}</td>
                        <td>{id}</td>
                        <td>{title}</td>
                        <td>{body}</td>
                        <td><button onClick={()=>this.delete(id)}>Delete</button></td>
                        <td><button>Update</button></td>
                        <td><button>View comments</button></td>
                        <td><button>View User</button></td>
                    </tr>    
                    );
    })
    }
                </tbody>
            </table>
        ) 
    }
}
export default demo;
